+    auto exec = this->get_executor();
+    if (new_solver) {
+        GKO_ASSERT_EQUAL_DIMENSIONS(new_solver, this);
+        GKO_ASSERT_IS_SQUARE_MATRIX(new_solver);
+        if (new_solver->get_executor() != exec) {
+            new_solver = gko::clone(exec, new_solver);
+        }
+    }
+    solver_ = new_solver;
